(function($) {
    Drupal.theme.prototype.sniperExampleButton = function(path, title) {
        return $('<a href="' + path + '" title="' + title + '">' + title + "</a>");
    };
    Drupal.behaviors.sniperExampleBehavior = {
        attach: function(context, settings) {
            $(".some-selector", context).once("foo", function() {
                var $anchor = Drupal.theme("sniperExampleButton", settings.myExampleLinkPath, settings.myExampleLinkTitle);
                $anchor.appendTo(this);
            });
        }
    };
})(jQuery);